sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {

  for (let i = 0; i < api.data.aggregation_query_1.output.data.GlideAggregate_Query.length; i++){

    if (api.data.aggregation_query_1.output.data.GlideAggregate_Query[i].groupBy[0].value === "scheduled"){
      return api.data.aggregation_query_1.output.data.GlideAggregate_Query[i].groupBy[0].value;
    }else{
      return 0;
    }
  }


}
